package com.eboshun.javabasics;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		// Integer types (whole numbers)
		
		int varInt = 1000000000 ;		
		/*
		 * Ints uses 32 bits in memory
		 * Ints value ranges from -2,147,483,648 to 2,147,483,647 (-2^31 to 2^31 - 1)
		 */
		System.out.println(varInt);
		
		
		byte varByte = 100;
		/*
		 *  Byte data type  uses 8 bits in memory
		 *   Bytes value ranges from -128 to 127 (2^7 TO 2^7 - 1
		 */
		System.out.println(varByte);
		  
		
		short varShort = 10000;
		/*
		 *  A short data type uses 16 bits in memory
		 *  its value ranges from -32,768 to 32,767 (-2^31 to 2^31 - 1)
		 */
		System.out.println(varShort);
		
		long varLong = 1000000000; //discover why this is throwing an error if I go over 9 zeroes
		/*
		 *  A long data type uses 8 bytes of memory
		 *  its value ranges from -9,233,372,036,854,755,808 to 9,233,372,036,854,755,807 
		 */
		System.out.println(varLong);
		
		// Decimal numbers
		
		float varFloat = 1.9999f;
		/*
		 *  32 bits in size good for 6 to 7 decimal places
		 */
		System.out.println(varFloat);
		
		float varDbl = 1.9999f;
		/*
		 *  64 bits in size good for 15 decimal places
		 */
		System.out.println(varDbl);
		
		// Others
		
		boolean varBoolean = true;
		/*
		 *  uses 1 bit in memory
		 *  holds a value of true or false
		 */
		System.out.println(varBoolean);
		
		
		char varChar = 'a';
		/*
		 *  character holds a single character
		 *  charfacter must be enclosed in single quotes
		 */
		System.out.println(varChar);
		
		String varStr = "Elbert";
		/*
		 *  A string holds multiple characters
		 *  A String ,ust be enclosed in double quotes
		 */
		System.out.println(varStr);
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		

	}

}
